import natural from 'natural';
import type { LicenseId } from '@license-auditor/data';

const tokenizer = new natural.WordTokenizer();

const createSet = (text: string) => {
  return new Set(tokenizer.tokenize(text));
};

const compare = (set1: Set<string>, set2: Set<string>) => {
  const intersection = new Set([...set1].filter(x => set2.has(x)));
  const union = new Set([...set1, ...set2]);
  const similarity = intersection.size / union.size;
  return similarity;
};
const getCalculateSimilarity = (library: Record<string, Set<string>>) => (textToCompare: string) => {
  const set = createSet(textToCompare);
  const similarities = Object.entries(library).map(([key, value]) => {
    return {
      licenseId: key as LicenseId,
      similarity: compare(set, value)
    };
  });

  return similarities;
};
const createLibrary = (texts: Record<string, string>) => {
  return Object.entries(texts).reduce<Record<string, Set<string>>>((library, [key, value]) => {
    library[key] = createSet(value);
    return library;
  }, {});
}

