const licensesLibrary = createLibrary(Object.entries(licenses).reduce<Record<string, string>>((acc, [licenseId, license]) => {
  acc[licenseId as LicenseId] = license.licenseText;
  return acc;
}, {}));
const calculateSimilarity = getCalculateSimilarity(licensesLibrary);

export function detectLicenses(licenseContent: string) {
  const similarities = calculateSimilarity(licenseContent);
  return similarities.sort((a, b) => b.similarity - a.similarity);
  // return similarities[0].licenseId;
}
